    .map(function (el) {
        let url = String(el.select('a:eq(0)').attr('href'))
        let doc = org.jsoup.Jsoup.parse(String(java.ajax(url)))
        let og = selector => doc.select('[property="og:' + selector + '"]').attr('content')
        return {
            name: og('novel:book_name'),
            author: og('novel:author'),
            kind: og('novel:category'),
            latest: og('novel:latest_chapter_name')+' • '+String(og('novel:update_time')),
            cover: og('image'),
            intro: og('description'),
            url:og('novel:read_url')
        }
    })

